import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { ArrowLeft, Terminal } from 'lucide-react';

const EasterEgg = () => {
  const [loaded, setLoaded] = useState(false);
  const [bootSequence, setBootSequence] = useState(0);
  const [typedText, setTypedText] = useState('');
  const [cursorVisible, setCursorVisible] = useState(true);
  const [accessGranted, setAccessGranted] = useState(false);
  const [userInput, setUserInput] = useState('');
  const inputRef = useRef(null);
  const terminalRef = useRef(null);

  // Terminal boot sequence text
  const bootMessages = [
    '> Inicializando sistema Hummand OS v3.5.0...',
    '> Carregando kernel...',
    '> Verificando integridade do sistema...',
    '> Estabelecendo conexão com servidores remotos...',
    '> Carregando módulos de segurança...',
    '> Inicializando interfaces de rede...',
    '> Verificando atualizações...',
    '> Carregando banco de dados municipal...',
    '> Inicializando interface de usuário...',
  ];

  // Access denied message
  const accessDeniedMessage = '❌ ACESSO NEGADO: Sistema em manutenção. Entre em contato com o administrador do sistema.';

  useEffect(() => {
    setLoaded(true);
    
    // Adiciona background com padrão sutilmente animado
    const canvas = document.getElementById('terminalBgCanvas');
    if (canvas) {
      const ctx = canvas.getContext('2d');
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      
      const squares = [];
      const numSquares = 100;
      
      for (let i = 0; i < numSquares; i++) {
        squares.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          size: Math.random() * 4 + 1,
          opacity: Math.random() * 0.03,
          speed: Math.random() * 0.2 + 0.1,
        });
      }
      
      function animate() {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        
        squares.forEach(square => {
          ctx.fillStyle = `rgba(220, 38, 38, ${square.opacity})`;
          ctx.fillRect(square.x, square.y, square.size, square.size);
          
          square.y += square.speed;
          
          if (square.y > canvas.height) {
            square.y = 0;
            square.x = Math.random() * canvas.width;
          }
        });
        
        requestAnimationFrame(animate);
      }
      
      animate();
      
      window.addEventListener('resize', () => {
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
      });
    }

    // Inicia a sequência de boot após um pequeno delay
    const timer = setTimeout(() => {
      typeBootSequence(0);
    }, 1000);

    // Animação do cursor piscante
    const cursorInterval = setInterval(() => {
      setCursorVisible(prev => !prev);
    }, 500);

    // Focus no input quando carregado
    if (inputRef.current) {
      inputRef.current.focus();
    }

    return () => {
      clearTimeout(timer);
      clearInterval(cursorInterval);
    };
  }, []);

  // Efeito para adicionar focus no input quando o usuário digita
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (bootSequence >= bootMessages.length && !accessGranted) {
        if (inputRef.current) {
          inputRef.current.focus();
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [bootSequence, accessGranted]);

  // Efeito para scroll automático do terminal
  useEffect(() => {
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  }, [typedText, userInput]);

  // Função para simular a digitação da sequência de boot
  const typeBootSequence = (index) => {
    if (index < bootMessages.length) {
      const currentMessage = bootMessages[index];
      let charIndex = 0;
      
      const typingInterval = setInterval(() => {
        if (charIndex <= currentMessage.length) {
          setTypedText(prev => prev + currentMessage.charAt(charIndex));
          charIndex++;
        } else {
          setTypedText(prev => prev + '\n\n');
          clearInterval(typingInterval);
          setBootSequence(index + 1);
          
          // Delay antes da próxima mensagem
          setTimeout(() => {
            typeBootSequence(index + 1);
          }, 300);
        }
      }, 30);
    } else {
      // Sequência de boot concluída, aguardando input do usuário
      setTypedText(prev => prev + '> Todos os sistemas ativos e prontos.\n\n' +
        '==================================================\n' +
        '             SISTEMA DE ADMINISTRAÇÃO PÚBLICA     \n' +
        '==================================================\n\n' +
        'Digite suas credenciais para acessar o sistema:\n\n'
      );
    }
  };

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleInputSubmit = (e) => {
    e.preventDefault();
    
    // Adiciona o comando digitado ao histórico do terminal
    setTypedText(prev => prev + '> ' + userInput + '\n\n');
    
    // Easter egg: se o usuário tentar qualquer comando
    if (userInput.trim().length > 0) {
      setTimeout(() => {
        setTypedText(prev => prev + accessDeniedMessage + '\n\n');
        setUserInput('');
      }, 500);
    }
  };

  const HummandLogo = () => (
    <svg 
      width="32" 
      height="32" 
      viewBox="0 0 36 36" 
      className="text-red-500 fill-current"
    >
      <path d="M18 0C8.059 0 0 8.059 0 18s8.059 18 18 18 18-8.059 18-18S27.941 0 18 0zm0 2c8.837 0 16 7.163 16 16s-7.163 16-16 16S2 26.837 2 18 9.163 2 18 2z" />
      <circle cx="13" cy="13" r="3" />
      <circle cx="23" cy="13" r="3" />
      <circle cx="13" cy="23" r="3" />
      <circle cx="23" cy="23" r="3" />
      <path d="M13 13L23 13 M13 13L13 23 M13 23L23 23 M23 13L23 23" stroke="currentColor" strokeWidth="1.5" fill="none" />
    </svg>
  );

  return (
    <div className="min-h-screen bg-[#0a0a0e] text-gray-300 font-sans relative overflow-hidden">
      {/* Background Canvas */}
      <canvas id="terminalBgCanvas" className="fixed top-0 left-0 w-full h-full -z-10"></canvas>
      
      {/* Header */}
      <header className="fixed w-full bg-black/50 backdrop-blur-md border-b border-red-900/20 z-50">
        <div className="container mx-auto">
          <nav className="flex items-center justify-between h-20 px-4 md:px-6">
            <Link to="/" className="flex items-center gap-3">
              <HummandLogo />
              <span className="text-xl font-bold tracking-wider text-white">HUMMAND</span>
            </Link>
            
            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center gap-6">
              <Link 
                to="/" 
                className="text-gray-300 hover:text-white transition-colors flex items-center gap-1"
              >
                <ArrowLeft size={16} />
                <span>Voltar</span>
              </Link>
            </div>

            {/* Mobile Navigation */}
            <div className="md:hidden">
              <Link 
                to="/" 
                className="text-gray-300 hover:text-white transition-colors flex items-center gap-1"
              >
                <ArrowLeft size={16} />
                <span>Voltar</span>
              </Link>
            </div>
          </nav>
        </div>
      </header>

      <main className="pt-20 min-h-screen relative">
        <div className={`transition-opacity duration-1000 ${loaded ? 'opacity-100' : 'opacity-0'}`}>
          <section className="py-16 px-4">
            <div className="max-w-4xl mx-auto">
              <div className="bg-black/70 backdrop-blur-sm border border-red-900/20 rounded-xl overflow-hidden shadow-2xl">
                {/* Terminal Header */}
                <div className="flex items-center justify-between px-4 py-3 border-b border-red-900/20 bg-black/80">
                  <div className="flex items-center gap-2">
                    <div className="w-3 h-3 rounded-full bg-red-500"></div>
                    <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
                    <div className="w-3 h-3 rounded-full bg-green-500"></div>
                  </div>
                  <div className="flex items-center gap-2 text-xs text-gray-400">
                    <Terminal size={14} className="text-red-500" />
                    <span>Hummand Terminal v3.5.0</span>
                  </div>
                  <div className="w-20"></div> {/* Espaçador para manter o header centralizado */}
                </div>
                
                {/* Terminal Content */}
                <div 
                  ref={terminalRef}
                  className="font-mono text-sm p-4 bg-black/90 h-[70vh] overflow-y-auto"
                >
                  <div className="text-gray-300 whitespace-pre-wrap">{typedText}</div>
                  
                  {bootSequence >= bootMessages.length && (
                    <form onSubmit={handleInputSubmit} className="flex items-center mt-2">
                      <span className="text-red-500 mr-2">$</span>
                      <input
                        ref={inputRef}
                        type="text"
                        value={userInput}
                        onChange={handleInputChange}
                        className="flex-1 bg-transparent outline-none border-none text-white font-mono"
                        autoFocus
                      />
                      <span className={`terminal-cursor ${cursorVisible ? 'opacity-100' : 'opacity-0'}`}></span>
                    </form>
                  )}
                </div>
                
                {/* Terminal Footer */}
                <div className="px-4 py-3 border-t border-red-900/20 bg-black/80 flex justify-between items-center">
                  <div className="text-xs text-gray-500">Sistema em manutenção</div>
                  <div className="flex items-center gap-2">
                    <div className="w-2 h-2 bg-red-500 rounded-full"></div>
                    <span className="text-xs text-red-500">Offline</span>
                  </div>
                </div>
              </div>
              
              {/* Informação adicional */}
              <div className="mt-8 text-center">
                <p className="text-gray-400 mb-4">
                  O sistema administrativo da Hummand está temporariamente indisponível.
                </p>
                <Link 
                  to="/contact" 
                  className="inline-flex items-center gap-2 px-6 py-2 bg-red-600 text-white hover:bg-red-500 rounded-md transition-all"
                >
                  <span>Entrar em Contato</span>
                  <ArrowLeft size={16} className="transform rotate-180" />
                </Link>
              </div>
            </div>
          </section>
          
          {/* Footer */}
          <footer className="py-8 px-4 bg-black border-t border-red-900/20 absolute bottom-0 w-full">
            <div className="max-w-6xl mx-auto flex flex-col md:flex-row justify-between items-center text-sm text-gray-500">
              <div>© 2025 Hummand. Todos os direitos reservados.</div>
              <div className="flex gap-8 my-4 md:my-0">
                <a href="mailto:contato@hummand.com.br" className="text-gray-400 hover:text-white transition-colors">
                  contato@hummand.com.br
                </a>
                <a href="tel:+551134567890" className="text-gray-400 hover:text-white transition-colors">
                  (11) 3456-7890
                </a>
              </div>
              <div>Site por <a href="https://viniciuslisboa.com.br" className="text-red-400 hover:text-red-300">Vinícius Lisboa</a></div>
            </div>
          </footer>
        </div>
      </main>
    </div>
  );
};

export default EasterEgg;
