// src/components/EasterEgg.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const EasterEgg = () => {
  const [eyePulse, setEyePulse] = useState(false);

  useEffect(() => {
    const pulseInterval = setInterval(() => {
      setEyePulse(true);
      setTimeout(() => setEyePulse(false), 1000);
    }, 4000);

    return () => clearInterval(pulseInterval);
  }, []);

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <div className="bg-[#0a0606] max-w-[400px] w-full relative">
        {/* Cabeçalho Amarelo */}
        <div className="bg-yellow-400 py-3 px-4 w-full">
          <h1 className="text-black text-2xl font-bold text-center tracking-wide">
            ATENÇÃO!
          </h1>
        </div>

        {/* Conteúdo Principal */}
        <div className="p-8 space-y-8">
          {/* Título */}
          <h2 className="text-xl font-mono font-bold text-red-800 text-center tracking-wider">
            ÁREA DE DÉJÀ VU
          </h2>
          
          {/* Texto Superior */}
          <div className="space-y-6">
            <p className="text-center font-mono text-gray-500">
              ESTA É SUA PRIMEIRA VEZ AQUI.
            </p>

            <p className="text-center font-mono text-gray-500">
              SE ESTA PLATAFORMA PARECE
              <br />
              FAMILIAR, ALERTE
              <br />
              IMEDIATAMENTE UM DESENVOLVEDOR.
            </p>
          </div>

          {/* Círculo/Olho */}
          <div className="flex justify-center py-6">
            <div className={`w-24 h-24 relative transition-all duration-500 ${eyePulse ? 'scale-110' : 'scale-100'}`}>
              {/* Círculo Externo Vermelho */}
              <div className="absolute inset-0 border-2 border-red-800 rounded-full"></div>
              {/* Círculo Interno Preto */}
              <div className="absolute inset-2 bg-black rounded-full flex items-center justify-center">
                {/* Halo Vermelho */}
                <div className={`absolute inset-0 bg-red-900/20 rounded-full transition-opacity duration-500 ${eyePulse ? 'opacity-50' : 'opacity-20'}`}></div>
                {/* Centro do Olho */}
                <div className="absolute inset-4 bg-red-900/40 rounded-full"></div>
              </div>
            </div>
          </div>

          {/* Texto Inferior */}
          <p className="text-center font-mono text-gray-500">
            SE VOCÊ VIR ALGO,
            <br />
            DIGA ALGO.
          </p>
        </div>

        {/* Rodapé Amarelo */}
        <div className="bg-yellow-400 py-3 px-4 w-full">
          <Link to="/">
            <p className="text-black text-center text-sm font-mono hover:underline cursor-pointer">
              Retorne imediatamente para o site da Hummand!
            </p>
          </Link>
        </div>
      </div>

      {/* Overlay de Escurecimento Gradual nas Bordas */}
      <div className="fixed inset-0 pointer-events-none bg-gradient-to-b from-black via-transparent to-black opacity-50"></div>
    </div>
  );
};

export default EasterEgg;
