// src/components/EasterEgg.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const EasterEgg = () => {
  const [glitchText, setGlitchText] = useState(false);
  const [eyePulse, setEyePulse] = useState(false);

  useEffect(() => {
    // Efeito sutil de glitch no texto
    const glitchInterval = setInterval(() => {
      setGlitchText(prev => !prev);
    }, 5000);

    // Efeito de pulsar do olho
    const pulseInterval = setInterval(() => {
      setEyePulse(true);
      setTimeout(() => setEyePulse(false), 1000);
    }, 4000);

    return () => {
      clearInterval(glitchInterval);
      clearInterval(pulseInterval);
    };
  }, []);

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <div className="bg-[#0a0606] max-w-[400px] w-full p-8 rounded relative">
        {/* Linha vermelha superior */}
        <div className="absolute top-0 left-0 right-0 h-[2px] bg-red-800"></div>
        
        <div className="space-y-8">
          <h1 className={`text-2xl font-mono font-black text-red-800 text-center tracking-wider ${glitchText ? 'opacity-90' : ''}`}>
            ÁREA DE DÉJÀ VU
          </h1>
          
          <p className="text-center font-mono text-gray-500 text-base">
            ESTA É SUA PRIMEIRA VEZ AQUI.
          </p>

          <p className="text-center font-mono text-gray-500 text-base leading-relaxed">
            SE ESTA PLATAFORMA PARECE
            <br />
            FAMILIAR, ALERTE
            <br />
            IMEDIATAMENTE UM DESENVOLVEDOR.
          </p>

          <div className="bg-[#100404] h-[120px] rounded-sm relative flex items-center justify-center my-8">
            <div className={`w-24 h-24 rounded-full border-2 border-red-900 absolute transition-all duration-500 ${eyePulse ? 'scale-110 opacity-50' : 'scale-100 opacity-100'}`}></div>
            <div className="w-24 h-24 flex items-center justify-center">
              <div className="w-16 h-16 rounded-full bg-red-900/20 flex items-center justify-center">
                <div className={`w-12 h-12 rounded-full bg-red-900/40 transition-transform duration-500 ${eyePulse ? 'scale-110' : 'scale-100'}`}>
                  <div className="w-full h-full rounded-full bg-[#200808] flex items-center justify-center">
                    <div className="w-4 h-4 rounded-full bg-red-900/80"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <p className="text-center font-mono text-gray-500 text-base">
            SE VOCÊ VIR ALGO,
            <br />
            DIGA ALGO.
          </p>

          <Link to="/" className="block mt-8">
            <p className="text-center text-xs font-mono text-red-800 hover:text-red-700 transition-colors cursor-pointer">
              Retorne imediatamente para o site da Hummand!
            </p>
          </Link>
        </div>
      </div>

      <style jsx>{`
        @keyframes subtle-blur {
          0% { filter: blur(0px); }
          50% { filter: blur(0.5px); }
          100% { filter: blur(0px); }
        }

        .bg-noise {
          background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%' height='100%' filter='url(%23noise)'/%3E%3C/svg%3E");
          animation: subtle-blur 5s infinite;
        }
      `}</style>
    </div>
  );
};

export default EasterEgg;
