// src/components/EasterEgg.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const EasterEgg = () => {
  const [eyeBlink, setEyeBlink] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setEyeBlink(true);
      setTimeout(() => setEyeBlink(false), 200);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <div className="relative max-w-[380px] w-full bg-zinc-100 rounded-lg shadow-2xl overflow-hidden">
        {/* Título Superior */}
        <div className="bg-red-600 text-white py-3 px-6 rounded-t-lg">
          <h1 className="text-2xl font-bold text-center">
            ÁREA DE DÉJÀ VU
          </h1>
        </div>

        {/* Conteúdo Principal */}
        <div className="space-y-6 p-6">
          {/* Primeiro Bloco de Texto */}
          <div className="bg-zinc-900 text-white p-4 rounded-lg">
            <p className="text-center font-medium">
              ESTA É SUA PRIMEIRA VEZ AQUI.
            </p>
            <p className="text-center font-medium mt-2">
              SE ESTA ÁREA PARECE FAMILIAR,
              <br />
              ALERTE IMEDIATAMENTE UM
              <br />
              DESENVOLVEDOR
            </p>
          </div>

          {/* Ícone Central */}
          <div className="bg-red-600 w-32 h-32 mx-auto rounded-lg p-4 flex items-center justify-center">
            <div className="relative w-full h-full">
              {/* Olho */}
              <div className={`absolute inset-0 transition-transform duration-200 ${eyeBlink ? 'scale-y-0' : 'scale-100'}`}>
                {/* Círculo externo com marcações */}
                <svg viewBox="0 0 100 100" className="w-full h-full">
                  <circle cx="50" cy="50" r="45" fill="none" stroke="black" strokeWidth="4"/>
                  {/* Marcações de tempo */}
                  {[...Array(12)].map((_, i) => (
                    <line
                      key={i}
                      x1="50"
                      y1="10"
                      x2="50"
                      y2="15"
                      stroke="black"
                      strokeWidth="3"
                      transform={`rotate(${i * 30} 50 50)`}
                    />
                  ))}
                  {/* Olho central */}
                  <circle cx="50" cy="50" r="20" fill="black"/>
                  {/* Seta */}
                  <path
                    d="M50,15 L45,10 L50,5 L55,10 L50,15"
                    fill="black"
                    transform="rotate(0 50 50)"
                  />
                </svg>
              </div>
            </div>
          </div>

          {/* Texto Inferior */}
          <p className="text-center text-lg font-medium">
            SE VOCÊ <span className="text-red-600 font-bold">VIR</span> ALGO,
            <br />
            <span className="text-red-600 font-bold">DIGA</span> ALGO
          </p>
        </div>

        {/* Rodapé */}
        <Link to="/">
          <div className="bg-zinc-900 text-white py-2 px-4 text-center text-sm">
            Retorne imediatamente para o site da Hummand!
          </div>
        </Link>
      </div>
    </div>
  );
};

export default EasterEgg;
