// src/components/Contact.js
import React, { useState, useEffect } from 'react';
import { Terminal, Send, MapPin } from 'lucide-react';
import { Link } from 'react-router-dom';
import './styles/contact.css';

const Contact = () => {
  const [loaded, setLoaded] = useState(false);
  const [formState, setFormState] = useState({
    name: '',
    email: '',
    message: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  useEffect(() => {
    setLoaded(true);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    try {
      // Simulação de envio - substitua pela sua API
      await new Promise(resolve => setTimeout(resolve, 2000));
      setSubmitStatus('success');
      setFormState({ name: '', email: '', message: '' });
    } catch (error) {
      console.error('Erro ao enviar mensagem:', error);
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
      setTimeout(() => setSubmitStatus(null), 3000);
    }
  };

  const locationArt = `
    [HUMMAND LAB]
    ===============
    Curral de Cima
    Paraíba - Brasil
    ===============
  `;

  return (
    <div className="min-h-screen bg-black text-gray-300 font-mono">
      <div className="grid-bg fixed inset-0 opacity-5"></div>

      {/* Header */}
      <header className="fixed w-full bg-black/90 backdrop-blur-sm border-b border-gray-800/50 z-50">
        <div className="container mx-auto">
          <nav className="flex items-center justify-between h-16 px-6">
            <Link to="/" className="flex items-center gap-3 float-element">
              <NeuralLogo />
              <span className="text-sm text-indigo-800 font-semibold tracking-wider">HUMMAND</span>
            </Link>
            <div className="flex items-center gap-4">
              <Link 
                to="/contact" 
                className="px-4 py-1.5 text-xs border border-indigo-900 text-indigo-400 hover:bg-indigo-900/20 rounded transition-colors"
              >
                CONTATO
              </Link>
              <a 
                href="https://app.hummand.com" 
                className="px-4 py-1.5 text-xs bg-indigo-900 text-indigo-100 hover:bg-indigo-800 rounded transition-colors"
              >
                ACESSAR SISTEMA
              </a>
            </div>
          </nav>
        </div>
      </header>

      {/* Main Contact Interface */}
      <main className="pt-16 min-h-screen relative">
        <div className={`transition-opacity duration-1000 ${loaded ? 'opacity-100' : 'opacity-0'}`}>
          {/* Terminal Header */}
          <div className="p-6 border-b border-gray-800/50">
            <div className="max-w-4xl mx-auto">
              <div className="flex items-center gap-2 mb-2 text-indigo-400">
                <Terminal className="w-4 h-4" />
                <span className="text-xs typing-effect">Terminal Hummand v2.5.0</span>
              </div>
              <div className="command-line">
                <span className="text-gray-500">$</span>
                <span className="text-indigo-400"> init</span>
                <span className="text-gray-400"> contact-system</span>
              </div>
            </div>
          </div>

          {/* Contact Form Section */}
          <div className="p-6">
            <div className="max-w-4xl mx-auto space-y-12">
              {/* Location Section */}
              <div className="space-y-4">
                <div className="text-xs text-gray-500 typing-effect">&gt; Carregando localização...</div>
                <div className="location-card p-4 bg-gray-900/30 border border-gray-800/50 rounded">
                  <div className="flex items-center gap-2 mb-4">
                    <MapPin className="w-4 h-4 text-indigo-400" />
                    <span className="text-sm text-indigo-400">Location Status: ONLINE</span>
                  </div>
                  <pre className="ascii-art text-xs text-gray-400 whitespace-pre-wrap">{locationArt}</pre>
                </div>
              </div>

              {/* Contact Form */}
              <div className="space-y-4">
                <div className="text-xs text-gray-500 typing-effect">&gt; Iniciando sistema de mensagens...</div>
                <form onSubmit={handleSubmit} className="space-y-6 processing">
                  <div className="command-line space-y-2">
                    <label className="block text-xs text-gray-500 typing-effect">&gt; Digite seu nome:</label>
                    <div className="flex items-center gap-2">
                      <span className="text-gray-500">$</span>
                      <input
                        type="text"
                        value={formState.name}
                        onChange={(e) => setFormState({...formState, name: e.target.value})}
                        className="command-input flex-1 bg-transparent text-indigo-400 outline-none text-sm"
                        required
                      />
                    </div>
                  </div>

                  <div className="command-line space-y-2">
                    <label className="block text-xs text-gray-500 typing-effect">&gt; Digite seu email:</label>
                    <div className="flex items-center gap-2">
                      <span className="text-gray-500">$</span>
                      <input
                        type="email"
                        value={formState.email}
                        onChange={(e) => setFormState({...formState, email: e.target.value})}
                        className="command-input flex-1 bg-transparent text-indigo-400 outline-none text-sm"
                        required
                      />
                    </div>
                  </div>

                  <div className="command-line space-y-2">
                    <label className="block text-xs text-gray-500 typing-effect">&gt; Digite sua mensagem:</label>
                    <div className="flex items-start gap-2">
                      <span className="text-gray-500">$</span>
                      <textarea
                        value={formState.message}
                        onChange={(e) => setFormState({...formState, message: e.target.value})}
                        className="command-input flex-1 bg-transparent text-indigo-400 outline-none text-sm min-h-[100px] resize-none"
                        required
                      />
                    </div>
                  </div>

                  <div className="flex justify-end">
                    <button
                      type="submit"
                      disabled={isSubmitting}
                      className="submit-btn px-6 py-2 bg-indigo-900 text-indigo-100 hover:bg-indigo-800 rounded transition-colors flex items-center gap-2"
                    >
                      <Send className="w-4 h-4" />
                      <span className="text-sm">
                        {isSubmitting ? 'Processando...' : 'Enviar Mensagem'}
                      </span>
                    </button>
                  </div>
                </form>

                {/* Submit Status */}
                {submitStatus === 'success' && (
                  <div className="status-message text-xs text-emerald-500">
                    &gt; Mensagem enviada com sucesso! Em breve entraremos em contato.
                  </div>
                )}
                {submitStatus === 'error' && (
                  <div className="status-message text-xs text-red-500">
                    &gt; Erro ao enviar mensagem. Por favor, tente novamente.
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Footer */}
          <footer className="border-t border-gray-800/50 p-6 mt-12">
            <div className="max-w-4xl mx-auto flex justify-between items-center text-xs text-gray-500">
              <div>© 2025 Hummand. Site por <a href="https://viniciuslisboa.com.br" className="text-indigo-400 hover:text-indigo-300">Vinícius Lisboa</a></div>
              <div className="flex gap-4">
                <a href="https://instagram.com/lab.hummand" className="hover:text-indigo-400 transition-colors">Instagram</a>
                <a href="https://linkedin.com/company/hummand" className="hover:text-indigo-400 transition-colors">LinkedIn</a>
              </div>
            </div>
          </footer>
        </div>
      </main>
    </div>
  );
};

export default Contact;
