// src/components/EasterEgg.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const EasterEgg = () => {
  const [eyeBlink, setEyeBlink] = useState(false);
  const [glitchText, setGlitchText] = useState(false);

  useEffect(() => {
    const blinkInterval = setInterval(() => {
      setEyeBlink(true);
      setTimeout(() => setEyeBlink(false), 200);
    }, 5000);

    const glitchInterval = setInterval(() => {
      setGlitchText(true);
      setTimeout(() => setGlitchText(false), 200);
    }, 3000);

    return () => {
      clearInterval(blinkInterval);
      clearInterval(glitchInterval);
    };
  }, []);

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4 overflow-hidden relative">
      {/* Camadas de névoa e efeitos */}
      <div className="fixed inset-0 fog-layer"></div>
      <div className="fixed inset-0 fog-layer-2"></div>
      <div className="fixed inset-0 fog-layer-3"></div>
      <div className="fixed inset-0 tv-noise opacity-30 pointer-events-none"></div>
      <div className="fixed inset-0 scanlines pointer-events-none"></div>
      <div className="fixed inset-0 vignette pointer-events-none"></div>

      <div className="relative max-w-[400px] w-full shadow-2xl z-10 aged-sign overflow-hidden">
        {/* Overlays de envelhecimento */}
        <div className="absolute inset-0 aged-overlay pointer-events-none z-20"></div>
        <div className="absolute inset-0 rust-spots pointer-events-none z-20"></div>

        <div className="header-warning">
  <h1 className={`text-3xl font-black text-black grid grid-cols-5 gap-4 px-8 py-2 text-center ${glitchText ? 'glitch' : ''}`}>
    <span>A</span>
    <span>T</span>
    <span>E</span>
    <span>N</span>
      <span>Ç</span>
      <span>Ã</span>
      <span>O</span>
      <span>!</span>
  </h1>
</div>

        <div className="p-8 space-y-8">
          <h2 className="text-2xl font-mono font-bold text-red-800 text-center tracking-wider">
            ÁREA DE DÉJÀ VU
          </h2>

          <div className="space-y-6">
            <p className="text-center font-mono text-gray-900">
              ESTA É SUA PRIMEIRA VEZ AQUI.
            </p>

            <p className="text-center font-mono text-gray-900 leading-relaxed">
              SE ESTA PLATAFORMA PARECE FAMILIAR,
              <br />
              ALERTE IMEDIATAMENTE UM DESENVOLVEDOR.
            </p>
          </div>

          {/* Enhanced Eye */}
          <div className="flex justify-center py-6">
            <div className={`w-32 h-32 relative ${eyeBlink ? 'scale-y-0' : 'scale-100'} transition-transform duration-200`}>
              <div className="absolute inset-0 bg-red-900/20 rounded-full animate-pulse"></div>
              <div className="absolute inset-2 bg-black rounded-full overflow-hidden">
                {[...Array(6)].map((_, i) => (
                  <div
                    key={i}
                    className="absolute w-full h-0.5 bg-red-900/30"
                    style={{
                      top: '50%',
                      transform: `rotate(${i * 30}deg)`,
                      transformOrigin: 'center'
                    }}
                  ></div>
                ))}
                <div className="absolute inset-4 bg-red-900/40 rounded-full animate-pulse">
                  <div className="absolute inset-2 bg-black rounded-full">
                    <div className="absolute w-2 h-2 bg-red-500 rounded-full top-1 left-1"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <p className="text-center font-mono text-gray-900">
            SE VOCÊ VIR ALGO,
            <br />
            DIGA ALGO.
          </p>
        </div>

        {/* Footer */}
        <div className="header-warning">
          <Link to="/">
            <p className="text-black text-center text-sm font-mono hover:underline">
              Retorne imediatamente para o site da Hummand!
            </p>
          </Link>
        </div>
      </div>

