// src/components/EasterEgg.js
import React from 'react';
import { Link } from 'react-router-dom';
import { Eye } from 'lucide-react';

const EasterEgg = () => {
  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <div className="relative max-w-md w-full p-8 rounded-lg shadow-lg overflow-hidden" 
        style={{
          background: 'linear-gradient(135deg, #000, #1a1a1a)', 
          border: '2px solid #a31ce6',
          boxShadow: '0 0 20px 5px #5e0a80'
        }}
      >
        <div 
          className="absolute top-0 left-0 right-0 bottom-0 pointer-events-none"
          style={{
            background: 'url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAANklEQVQoU2NkIBIwEqmOAa6wgZKADZnm/3A2zEAGYNTL6hK6YnRFcMXoijlIRXPQ9jNSGhgAbPMg2CXJE1sAAAAASUVORK5CYII=") repeat',
            opacity: 0.2
          }}
        ></div>

        <div className="relative z-10 space-y-6">
          <h1 className="text-2xl font-mono font-bold text-center" 
            style={{
              color: '#ff99fd', 
              textShadow: '0 0 10px #ff00ea, 0 0 20px #ff00ea, 0 0 30px #ff00ea, 0 0 40px #ff00ea'
            }}
          >
            ADVERTÊNCIA: ANOMALIA TEMPORAL
          </h1>

          <p className="text-center font-mono text-sm italic" style={{color: '#d898e8'}}>
            Distorções na realidade foram observadas nesta área.
          </p>

          <p className="text-center font-mono text-sm" style={{color: '#d1bcff'}}>
            Proceda com extrema cautela. Confiar nos próprios sentidos pode ser perigoso.  
          </p>

          <div className="bg-purple-700 bg-opacity-50 p-6 rounded-lg flex items-center justify-center relative">
            <div className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-1/3">
              <div className="bg-purple-900 bg-opacity-75 p-4 rounded-full inline-block ring-4 ring-purple-700 ring-opacity-75">
                <div className="bg-fuchsia-600 p-2 rounded-full inline-block">  
                  <Eye className="w-8 h-8 text-black" />
                </div>
              </div>
            </div>
          </div>

          <p className="text-center font-mono text-sm" style={{color: '#d898e8'}}>
            Se encontrar seu próprio doppelganger, não interaja. Procure ajuda imediatamente.
          </p>

          <p className="text-center font-mono text-xs italic" style={{color: '#b37dd4'}}>
            ATENÇÃO: Desvios do fluxo temporal normal podem se intensificar sem aviso.
          </p>

          <Link
            to="/"
            className="block text-center text-xs font-mono mt-8 px-4 py-2 rounded-md transition-colors duration-300"
            style={{
              color: '#ffbef5',
              background: '#3a1255',
              boxShadow: 'inset 0 0 8px 2px #5e0a80'
            }}
            onMouseEnter={(e) => {
              e.target.style.background = '#6f2a96';
              e.target.style.color = '#fff';
              e.target.style.boxShadow = 'inset 0 0 8px 2px #9a3dd3';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = '#3a1255';
              e.target.style.color = '#ffbef5';
              e.target.style.boxShadow = 'inset 0 0 8px 2px #5e0a80';
            }}
          >
            Retorne imediatamente para o site da Humanind!
          </Link>
        </div>
      </div>
    </div>
  );
};

export default EasterEgg;
